
const btnPrev=document.querySelector('.slider__prev'),btnNext=document.querySelector('.slider__next'),slaidItems=document.querySelectorAll('.slider__items');btnNext.addEventListener('click',(e)=>{e.preventDefault();slaidItems.forEach(item=>{if(item.classList.contains('slider_prev')){item.classList.remove('slider_prev');item.classList.add('slider_hiden');}
if(item.classList.contains('slider_active')){item.classList.remove('slider_active');item.classList.add('slider_prev');}
if(item.classList.contains('slider_next')){item.classList.remove('slider_next');item.classList.add('slider_active');}
if(item.classList.contains('slider_hiden')){item.classList.remove('slider_hiden');item.classList.add('slider_next');}});});btnPrev.addEventListener('click',(e)=>{e.preventDefault();slaidItems.forEach(item=>{if(item.classList.contains('slider_next')){item.classList.remove('slider_next');item.classList.add('slider_hiden');}
if(item.classList.contains('slider_active')){item.classList.remove('slider_active');item.classList.add('slider_next');}
if(item.classList.contains('slider_prev')){item.classList.remove('slider_prev');item.classList.add('slider_active');}
if(item.classList.contains('slider_hiden')){item.classList.remove('slider_hiden');item.classList.add('slider_prev');}});});